import SwiftUI
import SFSafeSymbols

struct FileRowLabel: View {
    @Environment(\.diclosureGroupDepth) private var depth

    var name: String
    var updatedAt: Date
    var isInTrash: Bool
    
    var nameTrailingView: AnyView
    
    init<T: View>(
        name: String,
        updatedAt: Date,
        isInTrash: Bool = false,
        @ViewBuilder nameTrailingView: () -> T = {
            EmptyView()
        }
    ) {
        self.name = name
        self.updatedAt = updatedAt
        self.isInTrash = isInTrash
        self.nameTrailingView = AnyView(nameTrailingView())
    }
    
    var body: some View {
        
        HStack(spacing: 10) {
            ExcalidrawIconView()
                .frame(height: 14)
                .opacity(isInTrash ? 0.3 : 1.0)
            
            Text(name)
            
            Spacer(minLength: 0)
        }
        .lineLimit(1)
        .truncationMode(.middle)
        .padding(.leading, 14 + CGFloat(depth+1) * 8)
        .padding(.trailing, 6)
        .padding(.vertical, 6)
        .contentShape(Rectangle())
    }
}



